mapper.select:
SELECT product_id, P.image_url, category_id, P.brand_id, p.NAME as product_name, price, quantity, eco_deal_status, sale_percent, B.NAME as brand_name FROM PRODUCT P JOIN BRAND B on P.BRAND_ID = B.BRAND_ID
category_id = #{categoryId}
ORDER BY product_id DESC

mapper.update:
UPDATE product
SET quantity = quantity - #{quantity},
updated_at = CURRENT_TIMESTAMP
WHERE product_id = #{productId}
AND quantity >= #{quantity}
